    private void btnLoginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLoginActionPerformed
       try{
            objLogin.AksesData();
            objLogin.mUsername = txtUsername.getText();
            objLogin.mPassword = txtPassword.getText();
            String sql = "select * from admin where username= '"+txtUsername.getText()+"' and password= '"+txtPassword.getText()+"'";
            objLogin.Masuk();
            Statement stat = objLogin.conn.createStatement();
            ResultSet res = stat.executeQuery(sql);
            res.last();
            int i=res.getRow();
            if(i>0){
                clsformadmin tampilan = new clsformadmin();
                txtUsername.setText("");
                txtPassword.setText("");
                stat.close();
                dispose();
                
                HalamanMenu Menu = new HalamanMenu();
                Menu.setVisible(true);
            }else{
                JOptionPane.showMessageDialog(null,"Username atau Password Salah");
                System.out.println();
            }
        }catch(Exception e){
            
        }
    }//GEN-LAST:event_btnLoginActionPerformed

    private void txtUsernameKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtUsernameKeyPressed
        if(evt.getKeyCode() == KeyEvent.VK_ENTER){
            if(txtUsername.getText().isEmpty()){
                JOptionPane.showMessageDialog(null, "Masukan Username","informasi",JOptionPane.INFORMATION_MESSAGE);
                txtUsername.requestFocus();
            }else{
                txtUsername.requestFocus();
            }
        }
    }//GEN-LAST:event_txtUsernameKeyPressed

    private void txtUsernameKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtUsernameKeyTyped
        if(txtUsername.getText().length()>15){
            evt.consume();
        }
    }//GEN-LAST:event_txtUsernameKeyTyped

    private void formWindowActivated(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowActivated
        Dimension posisi = Toolkit.getDefaultToolkit().getScreenSize();
        int x = (posisi.width-this.getWidth())/2;
        int y = (posisi.height-this.getHeight())/2;
        this.setLocation(x,y);
    }//GEN-LAST:event_formWindowActivated

    private void toRegisterMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_toRegisterMouseClicked
        FormRegister daftar = new FormRegister();
        daftar.setVisible(true);
        daftar.pack();
        daftar.setLocationRelativeTo(null);
        daftar.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
        this.dispose();
    }//GEN-LAST:event_toRegisterMouseClicked

    private void txtExitMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_txtExitMouseClicked
        objLogin.Keluar();
        System.exit(0);
    }//GEN-LAST:event_txtExitMouseClicked

    private void txtPasswordKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtPasswordKeyPressed
       if(evt.getKeyCode() == KeyEvent.VK_ENTER){
            if(txtPassword.getText().isEmpty()){
                JOptionPane.showMessageDialog(null, "Masukan Password","informasi",JOptionPane.INFORMATION_MESSAGE);
                txtPassword.requestFocus();
            }else{
                txtPassword.requestFocus();
            }
        }
    }//GEN-LAST:event_txtPasswordKeyPressed

    private void txtPasswordKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtPasswordKeyTyped
        if(txtPassword.getText().length()>8){
            evt.consume();
        }
    }//GEN-LAST:event_txtPasswordKeyTyped

