    private void txtstockKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtstockKeyTyped
        if (Character.isAlphabetic (evt.getKeyChar())){
            evt.consume();   JOptionPane.showMessageDialog(null, "Masukan Hanya Angka", "Peringatan", JOptionPane.WARNING_MESSAGE);
        }
        if ( txtstock.getText().length() == 15 ) {
            evt.consume();   JOptionPane.showMessageDialog(null, "Masukan Tidak Boleh Lebih Dari 15", "Peringatan", JOptionPane.WARNING_MESSAGE);
        }
    }//GEN-LAST:event_txtstockKeyTyped

    private void bsaveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bsaveActionPerformed
        String sql = "insert into dbbarang values (?,?,?,?,?)";
        try {
            PreparedStatement stat = conn.prepareStatement(sql);
            stat.setString(1, txtkdbarang.getText());
            stat.setString(2, txtnamabarang.getText());
            stat.setString(3, txtkategori.getText());
            stat.setString(4, txtstock.getText());
            stat.setString(5, txthargabarang.getText());

            stat.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data Berhasil Disimpan");
            kosong();
            txtkdbarang.requestFocus();
            datatable();
        } catch (SQLException e) {
            JOptionPane.showMessageDialog(null, "Data Gagal Disimpan "+e);
        }
    }//GEN-LAST:event_bsaveActionPerformed

    private void beditActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_beditActionPerformed
        try {
            String sql = "update dbbarang set nama_barang=?, kategori=?,jumlah=?,harga_barang=? where kode_barang=?";
            PreparedStatement stat = conn.prepareStatement(sql);
            stat.setString(1, txtnamabarang.getText());
            stat.setString(2, txtkategori.getText());
            stat.setString(3, txtstock.getText());
            stat.setString(4, txthargabarang.getText());
            stat.setString(5, txtkdbarang . getText());

            stat.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data Berhasil Diubah");
            kosong();
            txtkdbarang.requestFocus();
            datatable();

        }catch (SQLException e) {
            JOptionPane.showMessageDialog(null, "Data Gagal Diubah "+e);
        }
    }//GEN-LAST:event_beditActionPerformed

    private void bhapusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bhapusActionPerformed
        int ok = JOptionPane.showConfirmDialog(null,"hapus","KOnfirmasi Dialog", JOptionPane.YES_NO_CANCEL_OPTION);
        if (ok==0) {
            String sql ="delete from dbbarang where kode_barang ='"+txtkdbarang.getText()+"'";
            try {
                PreparedStatement stat = conn.prepareStatement(sql);
                stat.executeUpdate();
                JOptionPane.showMessageDialog(null, "Data Berhasil Dihapus");
                kosong();
                txtkdbarang.requestFocus();
                datatable();
            }catch (SQLException e) {
                JOptionPane.showMessageDialog(null, "Data Gagal Dihapus" +e);
            }      
        }
    }//GEN-LAST:event_bhapusActionPerformed

