    private void tabelbarangmasukMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tabelbarangmasukMouseClicked
       int bar = tabelbarangmasuk.getSelectedRow();
        String a = tabelbarangmasuk.getValueAt(bar, 0).toString();
        String b = tabelbarangmasuk.getValueAt(bar, 1).toString();


        
        txtkdbarang.setText(a);
        txtnamabarang.setText(b); 

    }//GEN-LAST:event_tabelbarangmasukMouseClicked

    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
        Object[] Baris = {"Kode Barang","Nama Barang", "Kategori", "Stock Barang","Harga Barang"};
        tabmode = new DefaultTableModel(null, Baris);
        tabelbarangmasuk.setModel(tabmode);
        String sql = "SELECT * FROM dbbarang WHERE kode_barang like '%"+tCari.getText()+"%' OR nama_barang like '%"+tCari.getText()+"%'";
        try {
            java.sql.Statement stat = conn.createStatement();
            ResultSet hasil = stat.executeQuery(sql);
            while(hasil.next()){
                String a = hasil.getString("kode_barang");
                String b = hasil.getString("nama_barang");
                String c = hasil.getString("kategori");
                String d = hasil.getString("jumlah");
                String e = hasil.getString("harga_barang");

                String[] data={a,b,c,d,e};
                tabmode.addRow(data);
            }
        } catch (Exception e) {
        }
    }//GEN-LAST:event_jButton5ActionPerformed

